script.text:
  $(document).ready(function(){
    //images for thumbnails
$('.bxslider').bxSlider({
  buildPager: function(slideIndex){
    switch(slideIndex){
		case 0: return '1: Vision';
		case 1: return '2: Goals';
		case 2: return '3: Departments';
		case 3: return '4';
		case 4: return '5';
		case 5: return '6';
		case 6: return '7';
		case 7: return '8: Hiearchy';
		case 8: return '9';
		case 9: return '10';
		case 10: return '11';
		case 11: return '12';
		case 12: return '13';
		case 13: return '14';
    }
  }
});
  });

style.text:
  .bx-wrapper .bx-pager {
    bottom: -95px;
  }
    .bx-wrapper .bx-pager a {
    border: solid white 1px;
    display: block;
    margin: 0 5px;
    padding: 3px;
  }

  .bx-wrapper .bx-pager a:hover,
  .bx-wrapper .bx-pager a.active {
border: solid #5280DD 1px;
  }

  .bx-wrapper {
    margin-bottom: 120px;
  }
