import Head from 'next/head'
import styles from '../styles/Home.module.css'

const data = {
  sheds : [
    {
      id:'reynolds-example',
      deck:{
        floor:'advantech',
        width:96,
        length:144,
        insulate: false
      },
      walls:{
        width:96,
        length:144,
        stud:{
          height:72
        },
        insulate: false,
        electrical: false,
        layout:{
          a:[
            {type:'window', typeid:1, offset:0, order:1},
            {type:'door', typeid:1, offset:0, order:2},
            {type:'window', typeid:1, offset:0, order:3}
          ]
        }
      },
      roof: {
        type:'gable',
        insulate:false
      },
      siding: {
        type:'vinyl',
        color:'white'
      },
      roof:{
        type: 'shingle',
        color: 'black',
        vent: 'gable'
      },
      windows:[
        {id:1,type: 'standard', width:18, height:27, top:72.5, trim: false, box:false, shutters: false},
        {id:2, type: 'standard', width:18, height:27, top:72.5, trim: false, box:false, shutters: false}
      ],

      doors:[
        { id:1, type:'vinyl', width: 61.75, height:72.5, ramp:{width:52, length:60}}
      ],

      extras:[
        {type:'loft', width:36, length: 96 }
        ]

    }
  ]
}

const FloorPlan = function(props) {

  const opts = {
    strokeWidth:1
  }

  const windows = (props)=>{
    return (
      <>
      {props.windows.map(function(w){
        return <rect x={props.walls.length*.2} y={props.walls.width-10} height={10} width={18} stroke="gray" fill="gray"/>
      })}

     </>)
  }



  return (<svg height={props.walls.width} width={props.walls.length} viewBox={"0 0 "+ props.walls.width + " " + props.walls.length} 
    stroke-width={opts.strokeWidth}
    stroke-alignment="inner"
    >

     <line x1="0" x2={props.walls.length} y1={props.walls.width} y2={props.walls.width} stroke="blue" stroke-alignment="inner"/>
     <line x1="0" x2={props.walls.length} y1={props.walls.width-10} y2={props.walls.width-10} stroke="blue" stroke-alignment="inner"/>

     <line x1="0" x2="0" y1="0" y2={props.walls.width} stroke="orange" />
     <line x1="10" x2="10" y1="0" y2={props.walls.width} stroke="orange" />

     <line x1="0" x2={props.walls.length} y1="0" y2="0" stroke="red" />
     <line x1="0" x2={props.walls.length} y1="10" y2="10" stroke="red" />

     <line x1={props.walls.length} x2={props.walls.length} y1="0" y2={props.walls.width} stroke="purple"/>
     <line x1={props.walls.length-10} x2={props.walls.length-10} y1="0" y2={props.walls.width} stroke="purple"/>


     {windows(props)}


  </svg>)
}

export default function Shed() {
  return (
    <div className={styles.container}>
      <Head>
        <title>Shed Assistant</title>
        <link rel="icon" href="/favicon.ico" />
      </Head>

      <main className={styles.main}>
        <h1 className={styles.title}>
          Shed Assistant
        </h1>

        <p className={styles.description}>
          Work Orders
        </p>

        <div className={styles.grid}>

          {
            data.sheds.map(function(shed){
              return (          
              <div className={styles.card}>
                <h3>{shed.id}</h3>
                <p>{shed.walls.width/12}' x {shed.walls.length/12}'</p>
                <hr/>
                <FloorPlan {...shed}/>

              </div>
          )
            })
          }



        </div>
      </main>

      <footer className={styles.footer}>
        Created by Stephen Reynolds, Jr.
      </footer>
    </div>
  )
}
