import { useState } from 'react'
import styles from '../../styles/Revelation.module.css'
import Head from 'next/head'
import { useRouter } from 'next/router'
import Textbook from './textbook'
import Navigation from './navigation'

class Quiz extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
    	questions: [
		{q:"According to chapter 1, this book is a revelation of Whom?",a:"Jesus Christ", ref:66001001},
		{q:"",a:"", ref:""}
		]};
  }

  render() {
    return (
      <div>
        <h1>Hello, world!</h1>
        <h2>It is {this.state.date.toLocaleTimeString()}.</h2>
      </div>
    );
  }
}

export default function quiz() {

	const [ questions, setQuestions ] = useState()

	// Add an item to array

//setQuestions(questions => [...questions, {q:"New Questions",a:"new answer", ref:"66002001"}]);

	console.log(questions[0])
  const router = useRouter()
  const q = router.query.q? router.query.q:0

  return (
<div>
      <Head>
        <title>Quiz | Youth Revelation Study</title>
        <link rel="icon" href="/favicon.ico" />
        <meta charset="UTF-8" />
      </Head>

	<main>
    
    <ul>
    {questions.map(function(q,i){
    	return <li><blockquote style={{color:"green"}}><input type="text"onChange={(e)=>{
    		e.preventDefault()
    		let que = [...questions]
    		que[i].question = e.target.value
    		setQuestions(que)
    	}} value={q.q}/></blockquote><p style={{color:"red"}} contentEditable={true}>{q.a}</p></li>
    })}
    </ul>

    	<button onClick={()=>{
    		setQuestions([...questions, {q:"",a:"", ref:""}])
    	}}>add new question</button>

    	<div>{JSON.stringify(questions)}</div>

	</main>

<Navigation />
</div>

)

}

