import { useState, Component } from 'react'
import styles from '../styles/Revelation.module.css'
import Head from 'next/head'
import { withRouter } from "next/router"
import eStyles from './Editor.module.css'

import Line, {lineTypes, templates} from './Blocks/Line'

function NewLineForm({index, handleNewLine}){
  return (<div style={{backgroundColor:"#dcdcdc", color:"#dcdcdc", margin:"15px", width:"100%", textAlign:"center"}}><form style={{ background:"none"}} onSubmit={handleNewLine}>
              <input type="hidden" name="index" value={index}/>

                <select style={{background:"none"}} name="lineType" id={"lineTypes" + index} defaultValue="md">
                  {lineTypes.map(function(t){
                    return <option key={t} value={t}>{t}</option>
                  })}
          </select>
       

              <input style={{background:"none"}} type="submit" value="+" />
            </form></div>)
}

class Editor extends Component {
  constructor(props) {
    super(props);
    this.state = {
    	file:false,
    	lineTypes: lineTypes,
    	lines:[],
      lesson: 1
    }
  }

  componentDidMount() {
    let lines = []

    if(localStorage.getItem(this.props.router.query.course) === null){
   		lines = [];
    	localStorage.setItem('NEW', JSON.stringify(lines));
    }else{
    	lines = JSON.parse(localStorage.getItem(this.props.router.query.course));
    }

    this.setState({
    	lines: lines
    })
}

componentWillReceiveProps(newProps){
  if(newProps.router.query.course !== this.props.router.query.course){
    this.setState({lines:JSON.parse(localStorage.getItem(this.props.router.query.course))})
  }
}

  render() {
  	  const handleNewLine = this.handleNewLine.bind(this)
  	  const update = this.update.bind(this)
  	  const deleteLine = this.deleteLine.bind(this)
      console.log(this.state.lines)
      const lesson = this.state.lesson
    return (
	<div>
	      <Head>
	        <title>Block Editor | Youth Revelation Study</title>
	        <link rel="icon" href="/favicon.ico" />
	        <meta charset="UTF-8" />

	      </Head>

		<main>

    <h1 style={{borderBottom:"solid 2px gray", position:"fixed", top:"0", height:"75px", backgroundColor:"white", width:"75%", marginTop:"0"}}>Editor  <button onClick={this.save.bind(this)}>SAVE</button><span style={{float:"right", fontSize:".7rem"}}><input type="file" id="input-file" onChange={this.getFile.bind(this)} /></span>
    </h1>

    <div style={{height:"75px"}}></div>
	    
    	 <div className={eStyles.row}>
        <div className={eStyles.column + " " + eStyles.smallColumn}>

            Some Text in Column One

        </div>
        <div className={eStyles.column}>

                <NewLineForm index={0} handleNewLine={handleNewLine}/>

              {this.state.lines.map(function(line, i){
                if(line.lesson === lesson){
                  return <div key={i}><button style={{color:"red", marginLeft:"15px"}} onClick={(e)=>{
                          deleteLine(i)
                        }}>x</button><Line key={i} index={i} data={line} update={update} />
                        <NewLineForm index={i+1} handleNewLine={handleNewLine}/></div>
                  }
              })}

        </div>
      </div>



		</main>


</div>
    );
  }

save(){
	const txt = JSON.stringify(this.state.lines)
	localStorage.setItem(this.props.router.query.course, txt)

	// Start file download.
	this.download(this.props.router.query.course+".json",txt);
}

download(filename, text) {
  var element = document.createElement('a');
  element.setAttribute('href', 'data:text/plain;charset=utf-8,' + encodeURIComponent(text));
  element.setAttribute('download', filename);

  element.style.display = 'none';
  document.body.appendChild(element);

  element.click();

  document.body.removeChild(element);
}

getFile(event) {
	const input = event.target
  if ('files' in input && input.files.length > 0) {
	  const lines = this.placeFileContent(input.files[0]).then((lines)=>{
	  	this.setState({lines:lines})
	  })

  	}
}

placeFileContent(file) {
	return this.readFileContent(file).then(content => {
  	return JSON.parse(content)
  }).catch(error => console.log(error))
}

readFileContent(file) {
	const reader = new FileReader()
  return new Promise((resolve, reject) => {
    reader.onload = event => resolve(event.target.result)
    reader.onerror = error => reject(error)
    reader.readAsText(file)
  })
}

handleNewLine(e){
    e.preventDefault();

    const lineType = e.target.lineType.value
    const index = e.target.index.value

    let newState = {...this.state}
    newState.lines.splice(index, 0, templates[lineType])
    this.setState({...newState})
}


update(line, data){
    let newState = {...this.state}
    newState.lines[line] = {...data}
    this.setState(newState)
}

deleteLine(line){
    let newData = {...this.state}
    newData.lines.splice(line, 1);
    this.setState(newData)
}

}
export default withRouter(Editor)

/*
1-4 Ellyanna
5-11 Jeremiah
12-16 Benjamin
17-22 Rosemary

1. Summaries
2. Conclusing of Book 1 sentence

		<Quiz line={1} questions={this.state.questions} updateQuestion={updateQuestion}/>

    */
