import Head from 'next/head'
import styles from '../styles/Home.module.css'

const db = {
  sheds : [
    {
      id:'reynolds-example',
      deck:{
        floor:'advantech',
        width:144,
        length:120,
        insulate: false,
        oncenter:16
      },
      walls:{
        a: {
        length:144,
        stud:72,        
        openings:[
          {type:'window', style: 'standard', width:18, height:27, top:72.5, trim: false, box:false, shutters: false, offset:0},
          {type:'door', style:'vinyl', width: 61.75, height:72.5, position: 0, ramp:{width:52, length:60}},
          {type:'window', style: 'standard', width:18, height:27, top:72.5, trim: false, box:false, shutters: false, offset:0}
        ]
        },
        b: {
          length:144,        
          openings:[
            {type:'door', style: 'garage', width:84, height:84, top:84, offset:0}
          ]},
        c:{length:144,        
        openings:[
          {type:'window', style: 'standard', width:18, height:27, top:72.5, trim: false, box:false, shutters: false, offset:0},
          {type:'window', style: 'standard', width:18, height:27, top:72.5, trim: false, box:false, shutters: false, offset:0}
        ], loft:{width:36, length: 96}},
        d:{length:144,        
        openings:[
          {type:'window', style: 'standard', width:18, height:27, top:72.5, trim: false, box:false, shutters: false, offset:0},
          {type:'window', style: 'standard', width:18, height:27, top:72.5, trim: false, box:false, shutters: false, offset:0}
        ]},
        insulate: false,
        electrical: false
      },
      roof: {
        type:'gable',
        insulate:false
      },
      siding: {
        type:'vinyl',
        color:'white'
      },
      roof:{
        type: 'shingle',
        color: 'black',
        vent: 'gable'
      }

    }
  ]
  }

function processOpenings(shed){

	  const opts = {
	    strokeWidth:1,
	    wallSize: 6,
	    openingSize: 12,
	    positionOffset:9
	  }

	let garageDoors = 0
	let walls = ["a","b","c","d"]
	let w = []

    walls.map(function(wall){
		shed.walls[wall].openings.map(function(v,k){

			if(v.type === "door" && v.style==="garage"){
				garageDoors++;
				w.push(wall)
			}

			    let space = {
			      empty: 0,
			      solid: shed.walls[wall].length,
			      spacing: 0
			    };

			    shed.walls[wall].openings && shed.walls[wall].openings.map(function(o){
			      space.empty = space.empty + o.width
			      space.solid = space.solid-o.width
			    }) 

			    space.spacing = shed.walls[wall].openings && space.solid/(shed.walls[wall].openings.length+1)

			    let position = 0

			      switch(k){

			        case 0:
			          position = space.spacing
			          break;

			        case 1:
			          position = space.spacing + shed.walls[wall].openings[0].width + space.spacing
			          break;

			        case 2:
			          position = space.spacing + shed.walls[wall].openings[0].width + space.spacing + shed.walls[wall].openings[1].width+ space.spacing
			          break;
			      }


			  let text = "W"

		      if(v.type === "door" && v.style === "garage"){
		        text = "GAR"
		      }else if(v.type === "door"){
		      	text = "DR"
		      }

		    shed.openings = {
		    	garageDoors: garageDoors,
		    	garageDoorOn: w
		    }

			shed.walls[wall].openings[k].position = position
			shed.walls[wall].openings[k].text = text
			shed.walls[wall].space = space

			switch(wall){
				case "a":
					shed.walls[wall].openings[k].drawing = {x:position, y:shed.walls.b.length-3.5, textx:position, texty:shed.walls.b.length+3.5}
					break;

				case "b":
					shed.walls[wall].openings[k].drawing = {x:0, y:position, textx:0, texty:0}
					break;

				case "c":
					shed.walls[wall].openings[k].drawing = {x:position, y:0, textx:0, texty:0}
					break;

				case "d":
					shed.walls[wall].openings[k].drawing = {x:shed.walls.b.length, y:position, textx:0, texty:0}
					break;

			}

			if(wall === "a" || wall === "c"){

			}else{
				shed.walls[wall].openings[k].drawing = {x:100, y:10}
			}


		})
	})

	return shed
}

function processDecks(shed){

		{/*Plate Length*/}
		shed.deck.plates = {length: shed.deck.length}
   		{/*Joists*/}
        shed.deck.joists = {
        	length: shed.deck.width-3,
        	count: Math.ceil((shed.deck.length/shed.deck.oncenter)+1) + shed.openings.garageDoors,
        	layout: []
        }

		  let x = .75

		  while (x <= shed.deck.length){
		  	shed.deck.joists.layout.push(x)

		  	if(x === .75){
		  		if(shed.openings.garageDoorOn.includes('b')){
					shed.deck.joists.layout.push(2.25)
				}

				x = 0
		  	}

		  	if(x === shed.deck.length || (shed.deck.length-x) < shed.deck.oncenter){break;}
		  	x += shed.deck.oncenter

		  	if((x + shed.deck.oncenter) > shed.deck.length){ 
		  		const diff = shed.deck.length-(x-shed.deck.oncenter)		
		  		x = shed.deck.length - (diff/2)
		  	}
		  }    

		if(shed.openings.garageDoorOn.includes('d') ){
			shed.deck.joists.layout.push(shed.deck.length-2.25)
		}

		shed.deck.joists.layout.push(shed.deck.length-.75)

		return shed
}

function data(db){


	db.sheds.map(function(val, key){
		let shed = processOpenings(val)
		shed = processDecks(shed)
		db.sheds[key] = shed
	})

	console.log(db.sheds[0])
	return db
}

