import Head from 'next/head'
import styles from '../styles/Home.module.css'

const data = {
  sheds : [
    {
      id:'reynolds-example',
      deck:{
        floor:'advantech',
        width:96,
        length:144,
        insulate: false
      },
      walls:{
        a: {
        length:144,
        stud:72,        
        openings:[
          {type:'window', style: 'standard', width:18, height:27, top:72.5, trim: false, box:false, shutters: false, position:0},
          {type:'door', style:'vinyl', width: 61.75, height:72.5, position: 0, ramp:{width:52, length:60}},
          {type:'window', style: 'standard', width:18, height:27, top:72.5, trim: false, box:false, shutters: false, position:0}
        ]
        },
        b: {length:96},
        c:{length:144, loft:{width:36, length: 96}},
        d:{length:96},
        insulate: false,
        electrical: false
      },
      roof: {
        type:'gable',
        insulate:false
      },
      siding: {
        type:'vinyl',
        color:'white'
      },
      roof:{
        type: 'shingle',
        color: 'black',
        vent: 'gable'
      }

    }
  ]
}

function getOpenings(wall){
    let space = 0;

    wall.openings && wall.openings.map(function(o){
      space = space + o.width
    }) 

    return space
    }

const FloorPlan = function(props) {

  const opts = {
    strokeWidth:1,
    aOpen: getOpenings(props.walls.a),
    bOpen: getOpenings(props.walls.b),
    cOpen: getOpenings(props.walls.c),
    dOpen: getOpenings(props.walls.d)
  }


  return (<svg height={props.walls.width} width={props.walls.length} viewBox={"0 0 "+ props.walls.width + " " + props.walls.length} 
    stroke-width={opts.strokeWidth}
    stroke-alignment="inner"
    >

     <line x1="0" x2={props.walls.a.length} y1={props.walls.b.length} y2={props.walls.b.length} stroke="blue" stroke-alignment="inner"/>
     <line x1="0" x2={props.walls.a.length} y1={props.walls.b.length-10} y2={props.walls.b.length-10} stroke="blue" stroke-alignment="inner"/>

     {props.walls.a.openings.map(function(o){

      let position = 0
      let color = "gray"

      if(o.type === "door"){
        position = (props.walls.a.length*.5)-(o.width*.5)
        color = "teal"
      }else{

      }

      return <rect x={position} y={props.walls.b.length-10} height={10} width={o.width} stroke="gray" fill={color}/>
     })}

     <line x1="0" x2="0" y1="0" y2={props.walls.b.length} stroke="orange" />
     <line x1="10" x2="10" y1="0" y2={props.walls.b.length} stroke="orange" />

     <line x1="0" x2={props.walls.c.length} y1="0" y2="0" stroke="red" />
     <line x1="0" x2={props.walls.c.length} y1="10" y2="10" stroke="red" />

     <line x1={props.walls.a.length} x2={props.walls.a.length} y1="0" y2={props.walls.d.length} stroke="purple"/>
     <line x1={props.walls.a.length-10} x2={props.walls.a.length-10} y1="0" y2={props.walls.d.length} stroke="purple"/>





  </svg>)
}

export default function Shed() {
  return (
    <div className={styles.container}>
      <Head>
        <title>Shed Assistant</title>
        <link rel="icon" href="/favicon.ico" />
      </Head>

      <main className={styles.main}>
        <h1 className={styles.title}>
          Shed Assistant
        </h1>

        <p className={styles.description}>
          Work Orders
        </p>

        <div className={styles.grid}>

          {
            data.sheds.map(function(shed){
              return (          
              <div className={styles.card}>
                <h3>{shed.id}</h3>
                <p>{shed.walls.b.length/12}' x {shed.walls.a.length/12}'</p>
                <hr/>
                <FloorPlan {...shed}/>

              </div>
          )
            })
          }



        </div>
      </main>

      <footer className={styles.footer}>
        Created by Stephen Reynolds, Jr.
      </footer>
    </div>
  )
}
