import Head from 'next/head'
import styles from '../styles/Home.module.css'

const db = {
  sheds : [
    {
      id:'reynolds-example',
      deck:{
        floor:'advantech',
        width:144,
        length:132,
        insulate: false,
        oncenter:16
      },
      walls:{
        a: {
        length:144,
        stud:72,        
        openings:[
          {type:'window', style: 'standard', width:18, height:27, top:72.5, trim: false, box:false, shutters: false, position:0},
          {type:'door', style:'vinyl', width: 61.75, height:72.5, position: 0, ramp:{width:52, length:60}},
          {type:'window', style: 'standard', width:18, height:27, top:72.5, trim: false, box:false, shutters: false, position:0}
        ]
        },
        b: {
          length:144,        
          openings:[
            {type:'window', style: 'standard', width:18, height:27, top:72.5, trim: false, box:false, shutters: false, position:0}
          ]},
        c:{length:144,        
        openings:[
          {type:'window', style: 'standard', width:18, height:27, top:72.5, trim: false, box:false, shutters: false, position:0},
          {type:'window', style: 'standard', width:18, height:27, top:72.5, trim: false, box:false, shutters: false, position:0}
        ], loft:{width:36, length: 96}},
        d:{length:144,        
        openings:[
          {type:'window', style: 'standard', width:18, height:27, top:72.5, trim: false, box:false, shutters: false, position:0},
          {type:'window', style: 'standard', width:18, height:27, top:72.5, trim: false, box:false, shutters: false, position:0}
        ]},
        insulate: false,
        electrical: false
      },
      roof: {
        type:'gable',
        insulate:false
      },
      siding: {
        type:'vinyl',
        color:'white'
      },
      roof:{
        type: 'shingle',
        color: 'black',
        vent: 'gable'
      }

    }
  ]
  }

function processDecks(db){

	db.sheds.map(function(val, key){
		let shed = val
		 shed.deck.joists = []
		  let x = 0

		  while (x <= shed.deck.length){
		  	shed.deck.joists.push(x)
		  	if(x === shed.deck.length){break;}
		  	x += shed.deck.oncenter
		  	if((x + shed.deck.oncenter) > shed.deck.length){  		
		  		x = (x-shed.deck.oncenter) + (x+shed.deck.oncenter-shed.deck.length)
		  	}
		  }    

		console.log(shed)
		db.sheds[key] = shed
	})

	return db
}

function data(db){
	db = processDecks(db)
	return db
}

function getOpenings(wall){
    let space = {
      empty: 0,
      solid: wall.length,
      spacing: 0
    };

    wall.openings && wall.openings.map(function(o){
      space.empty = space.empty + o.width
      space.solid = space.solid-o.width
    }) 

    space.spacing = wall.openings && space.solid/(wall.openings.length+1)

    return space
    }

function Opening(props){

    let position = 0
    let spacing = getOpenings(props.wall).spacing

      switch(props.index){

        case 0:
          position = spacing
          break;

        case 1:
          position = spacing + props.wall.openings[0].width + spacing
          break;

        case 2:
          position = spacing + props.wall.openings[0].width + spacing + props.wall.openings[1].width+ spacing
          break;
      }

      let text = "W"

      if(props.opening.type === "door"){
        text = "DR"
      }

      let textposition = 0

      if(props.opening.width > 40){
        textposition = position + (position * .6)
      }else{
        textposition = position + 1
      }

      if(props.horizontal){
              return (<>
              <rect x={position} y={props.position} height={props.size} width={props.opening.width} stroke="gray" fill="white"/>
              <text x={textposition} y={props.position+11} fill="red">{text}</text></>)
            }else{

                if(props.opening.width > 40){
                  textposition = position + props.opening.width - (props.opening.width * .5)
                }else{
                  textposition = position + props.opening.width - (props.opening.width * .18)
                }


              return (<>
              <rect x={props.position-2} y={position} height={props.opening.width} width={props.size} stroke="gray" fill="white"/>
              <text x={props.position-2} y={textposition} fill="red">{text}</text></>)
            }

}

const FloorPlan = function(props) {

  const opts = {
    strokeWidth:1,
    wallSize: 6,
    openingSize: 12,
    positionOffset:9
  }

  return (<svg height={props.walls.b.length} width={props.walls.a.length} viewBox={"-10 -1 "+ (props.walls.a.length + 40) + " " + (props.walls.b.length)} 
    stroke-width={opts.strokeWidth}
    stroke-alignment="inner"
    >

     <rect x={opts.wallSize} y={props.walls.b.length-opts.wallSize} height={opts.wallSize} width={props.walls.a.length-(opts.wallSize*2)} stroke="black" fill="black"/>
     <text x={props.walls.a.length*.45} y={props.walls.b.length+15} fill="gray">A</text>
     {props.walls.a.openings.map(function(o,i){
        return <Opening wall={props.walls.a} opening={o} index={i} size={opts.openingSize} position={props.walls.b.length-opts.positionOffset} horizontal={true}/>
     })}

     <rect x="0" y={0} height={props.walls.b.length} width={opts.wallSize} stroke="black" fill="black"/>
     <text x={-11} y={props.walls.b.length*.45} fill="gray">B</text>
      {props.walls.b.openings.map(function(o,i){
        return <Opening wall={props.walls.b} opening={o} index={i} size={opts.openingSize} position={0} horizontal={false}/>
      })}

     <rect x={opts.wallSize} y={0} height={opts.wallSize} width={props.walls.c.length-(opts.wallSize*2)} stroke="black" fill="black"/>
     <text x={props.walls.a.length*.45} y={0} fill="gray">C</text>
    {props.walls.c.openings.map(function(o,i){
        return <Opening wall={props.walls.c} opening={o} index={i} size={opts.openingSize} position={-3} horizontal={true}/>
     })}

     <rect x={props.walls.a.length-(opts.wallSize)} y={0} height={props.walls.d.length} width={opts.wallSize} stroke="black" fill="black"/>
    <text x={props.walls.a.length+7} y={props.walls.d.length*.45} fill="gray">D</text>
    {props.walls.d.openings.map(function(o,i){
        return <Opening wall={props.walls.d} opening={o} index={i} size={opts.openingSize} position={props.walls.a.length-(opts.wallSize)} horizontal={false}/>
      })}

  </svg>)
}

const Deck = function(props) {

  const opts = {
    strokeWidth:1
  }

  const joists = []
  let x = 0

  while (x <= props.deck.length){
  	console.log(x)
  	let sub = 0
  	if(x !== 0 ){sub = .75}
  	joists.push(<rect x={x-sub} y={1.5} height={props.deck.width-1.5} width={1.5} stroke="black" fill="none"/>)
  	if(x === props.deck.length){break;}
  	x += props.deck.oncenter
/*
  	if((props.deck.length-x) < props.deck.oncenter){
  		x = props.deck.length
  	}else 
*/
  	if((x + props.deck.oncenter) > props.deck.length){  		
  		x = (x-props.deck.oncenter) + (x+props.deck.oncenter-props.deck.length)
  	}
  }    

  return (<svg height={props.deck.width} width={props.deck.length} viewBox={"-10 -1 "+ (props.deck.length + 40) + " " + (props.deck.width)} 
    stroke-width={opts.strokeWidth}
    stroke-alignment="inner"
    >
	{/*PLATES*/}
     <rect x={0} y={props.deck.width} height={1.5} width={props.deck.length} stroke="black" fill="none"/>
     <rect x={0} y={0} height={1.5} width={props.deck.length} stroke="black" fill="none"/>

 	{/*JOISTS*/}
     {joists}

  </svg>)
}

const WorkOrder = function(props) {

  return (<div>
      <h1>Work Order ({props.shed.id})</h1>

      <h2>DECK</h2>
        Size: <input type="text" value={props.shed.deck.width/12} /> (w) x <input type="text" value={props.shed.deck.length/12} /> (l) <br/>
        Insulate: <input type="checkbox" value={props.shed.deck.insulate} />
        <br/>
        Plate Length: {props.shed.deck.length}"
        <br/>
        Joists Length: {props.shed.deck.width-3}"
        <br/>
        Joists Count: {Math.ceil((props.shed.deck.length/props.shed.deck.oncenter)+1)}"

      <h2>Walls</h2>

    <textarea>{JSON.stringify(props.shed)}</textarea>
    </div>)
}

