import Head from 'next/head'
import styles from '../styles/Home.module.css'

const data = {
  sheds : [
    {
      id:'reynolds-example',
      deck:{
        floor:'advantech',
        width:96,
        length:144,
        insulate: false
      },
      walls:{
        a: {
        length:144,
        stud:72,        
        openings:[
          {type:'window', style: 'standard', width:18, height:27, top:72.5, trim: false, box:false, shutters: false, position:0},
          {type:'door', style:'vinyl', width: 61.75, height:72.5, position: 0, ramp:{width:52, length:60}},
          {type:'window', style: 'standard', width:18, height:27, top:72.5, trim: false, box:false, shutters: false, position:0}
        ]
        },
        b: {
          length:96,        
          openings:[
            {type:'window', style: 'standard', width:18, height:27, top:72.5, trim: false, box:false, shutters: false, position:0}
          ]},
        c:{length:144, loft:{width:36, length: 96}},
        d:{length:96},
        insulate: false,
        electrical: false
      },
      roof: {
        type:'gable',
        insulate:false
      },
      siding: {
        type:'vinyl',
        color:'white'
      },
      roof:{
        type: 'shingle',
        color: 'black',
        vent: 'gable'
      }

    }
  ]
}

function getOpenings(wall){
    let space = {
      empty: 0,
      solid: wall.length,
      spacing: 0
    };

    wall.openings && wall.openings.map(function(o){
      space.empty = space.empty + o.width
      space.solid = space.solid-o.width
    }) 

    space.spacing = wall.openings && space.solid/(wall.openings.length+1)

    return space
    }

function Opening(props){

    let position = 0
    let spacing = getOpenings(props.wall).spacing

      switch(props.index){

        case 0:
          position = spacing
          break;

        case 1:
          position = spacing + props.wall.openings[0].width + spacing
          break;

        case 2:
          position = spacing + props.wall.openings[0].width + spacing + props.wall.openings[1].width+ spacing
          break;
      }

      let text = "W"

      if(props.type === "door"){
        text = "D"
      }

      let textposition = 0

      if(props.width > 40){
        textposition = position + (position * .6)
      }else{
        textposition = position + 1
      }

      return (<>
      <rect x={position} y={props.y} height={props.wallSize} width={props.width} stroke="gray" fill="white"/>
      <text x={textposition} y={props.width} fill="red">{text}</text></>)
}

const FloorPlan = function(props) {

  const opts = {
    strokeWidth:1,
    wallSize: 6
  }


  return (<svg height={props.walls.width} width={props.walls.length} viewBox={"0 0 "+ props.walls.width + " " + props.walls.length} 
    stroke-width={opts.strokeWidth}
    stroke-alignment="inner"
    >

     <rect x={opts.wallSize} y={props.walls.b.length-opts.wallSize} height={opts.wallSize} width={props.walls.a.length-(opts.wallSize*2)} stroke="black" fill="black"/>

     {props.walls.a.openings.map(function(o,i){
        return <Opening wall={props.walls.a} {...o} index={i} wallSize={opts.wallSize+6} y={props.walls.b.length-10}/>
     })}

     <rect x="0" y={0} height={props.walls.b.length} width={opts.wallSize} stroke="black" fill="black"/>

     <rect x={opts.wallSize} y={0} height={opts.wallSize} width={props.walls.c.length-(opts.wallSize*2)} stroke="black" fill="black"/>

     <rect x={props.walls.a.length-(opts.wallSize)} y={0} height={props.walls.d.length} width={opts.wallSize} stroke="black" fill="black"/>

  </svg>)
}

export default function Shed() {
  return (
    <div className={styles.container}>
      <Head>
        <title>Shed Assistant</title>
        <link rel="icon" href="/favicon.ico" />
      </Head>

      <main className={styles.main}>
        <h1 className={styles.title}>
          Shed Assistant
        </h1>

        <p className={styles.description}>
          Work Orders
        </p>

        <div className={styles.grid}>

          {
            data.sheds.map(function(shed){
              return (          
              <div className={styles.card}>
                <h3>{shed.id}</h3>
                <p>{shed.walls.b.length/12}' x {shed.walls.a.length/12}'</p>
                <hr/>
                <FloorPlan {...shed}/>

              </div>
          )
            })
          }



        </div>
      </main>

      <footer className={styles.footer}>
        Created by Stephen Reynolds, Jr.
      </footer>
    </div>
  )
}
