import ReactMarkdown from 'react-markdown/with-html'

function Markdown(props){

	const { data, update, index, read, style } = props

	const lineStyle = {
		width:"100%", 
		height:"150px",
		...style
	}

	if(read){
		return <ReactMarkdown allowDangerousHtml style={lineStyle}>{data.value}</ReactMarkdown>
	}else{
	return (<textarea style={lineStyle} type="text" value={data.value} onChange={(e)=>{
		let l = {...data}
		l.value = e.target.value
		update(index, l)
	}}></textarea>)
}
}


export const markdownTemplate = { type:"md", value:"" }
export default Markdown;
