sourcesContent:
import { useState, Component } from 'react'
import styles from '../../styles/Revelation.module.css'
import Head from 'next/head'
import { withRouter } from "next/router"
import Textbook from './textbook'
import Navigation from './navigation'

class Quiz extends Component {
  constructor(props) {
    super(props);
    this.state = {
    	questions: [],
    	file:false
    }
  }

  componentDidMount() {
    let questions = []

    if(localStorage && localStorage.getItem('questions') === null){
   		questions = [
		{q:"According to chapter 1, this book is a revelation of Whom?",a:"Jesus Christ", ref:66001001},
		{q:"",a:"", ref:""}
		];
    	localStorage.setItem('questions', JSON.stringify(questions));
    }else{
    	questions = JSON.parse(localStorage.getItem('questions'));
    }

    this.setState({
    	questions: questions
    })
}

  render() {
  	  const q = this.props.router.query.q? this.props.router.query.q:0
  	  const updateQuestion = this.updateQuestion.bind(this)
  	  const handleSubmit = this.handleSubmit.bind(this)

    return (
	<div>
	      <Head>
	        <title>Quiz | Youth Revelation Study</title>
	        <link rel="icon" href="/favicon.ico" />
	        <meta charset="UTF-8" />
	      </Head>

		<main>
	    
		<button onClick={this.saveQuestions.bind(this)}>SAVE</button>

		<div>
		 <label htmlFor="input-file">import</label><br/>
		 <input type="file" id="input-file" onChange={this.getFile.bind(this)} />
		</div>

	    <ol>
	    {this.state.questions.map(function(q,i){
	    	return <li key={i}><textarea className={styles.question} type="text" onChange={
	    		(e)=>{
	    			updateQuestion("q",i,e.target.value)
	    		}} value={q.q}></textarea>
	    <textarea className={styles.answer} type="text"onChange={
	    		(e)=>{
	    			updateQuestion("a",i,e.target.value)
	    		}} value={q.a}></textarea>

	    		<br/><input type="text"/>:<input type="text"/><button>+</button>

	    		   <form onSubmit={handleSubmit}>
	    		   	<input type="hidden" name="index" value={i} />
			        <label>
			          Name:
			          <input type="text" name="name" />
			        </label>
			  		<label>
			          Value:
			          <input type="text" name="value"/>
			        </label>
			        <input type="submit" value="Submit" />
			      </form>

	    		</li>
	    })}
	    </ol>

	    	<button onClick={function(e){
	    		updateQuestion()
	    	}}>add new question</button>

	    	<div>{JSON.stringify(this.state.questions)}</div>

		</main>

	<Navigation />
</div>
    );
  }

  updateQuestion(propName, index, value){

  	let newQ = [...this.state.questions]

  	if(propName === undefined){
  		newQ.push({q:"",a:"", ref:""})
  	}else{
  		newQ[index][propName] = value
  	}
  	
  	this.setState({
	  questions: newQ
	})
  }

saveQuestions(){
	const txt = JSON.stringify(this.state.questions)
	localStorage.setItem('questions', txt)

	// Start file download.
	this.download("questions.json",txt);
}

download(filename, text) {
  var element = document.createElement('a');
  element.setAttribute('href', 'data:text/plain;charset=utf-8,' + encodeURIComponent(text));
  element.setAttribute('download', filename);

  element.style.display = 'none';
  document.body.appendChild(element);

  element.click();

  document.body.removeChild(element);
}

getFile(event) {
	const input = event.target
  if ('files' in input && input.files.length > 0) {
	  const questions = this.placeFileContent(input.files[0]).then((questions)=>{
	 	console.log(questions)
	  	this.setState({questions:questions})
	  })

  	}
}

placeFileContent(file) {
	return this.readFileContent(file).then(content => {
  	return JSON.parse(content)
  }).catch(error => console.log(error))
}

readFileContent(file) {
	const reader = new FileReader()
  return new Promise((resolve, reject) => {
    reader.onload = event => resolve(event.target.result)
    reader.onerror = error => reject(error)
    reader.readAsText(file)
  })
}

handleSubmit(e){
    e.preventDefault();
	console.log(e.target.name)    
}

}
export default withRouter(Quiz)

/*
1-4 Ellyanna
5-11 Jeremiah
12-16 Benjamin
17-22 Rosemary

1. Summaries
2. Conclusing of Book 1 sentence

    */
