import { useState, Component } from 'react'
import styles from '../../styles/Revelation.module.css'
import Head from 'next/head'
import { withRouter } from "next/router"
import Textbook from './textbook'
import Navigation from './navigation'

function Quiz({line, questions, updateQuestion, addQuestionProp}){
	return (<><ol>
	    {questions.map(function(q,i){

	    	const keys = Object.keys(q)
	    	return <li key={i}>

	    		{keys.map(function(k){
	    			return <div><textarea className={"questions " + styles[k]} type="text" onChange={
			    		(e)=>{
			    			updateQuestion(line,k,i,e.target.value)
			    		}} value={q[k]}></textarea><span className={styles.questionLabel}>{k}</span>

			    		<button style={{color:"red", marginLeft:"15px"}} onClick={(e)=>{
			    			updateQuestion(line, k,i,"delete")
			    		}}>x</button>
	    		</div>
	    		})}

	    		   <form onSubmit={addQuestionProp}>
	    		   <input type="hidden" name="line" value={line} />
	    		   	<input type="hidden" name="index" value={i} />
			        <label>
			          Key:
			          <input type="text" name="prop" />
			        </label>
			  		<label>
			          Value:
			          <input type="text" name="val"/>
			        </label>
			        <input type="submit" value="+" />
			      </form>

			      <hr/>
	    		</li>
	    })}
	    </ol>

	    	<button onClick={function(e){
	    		updateQuestion(line)
	    	}}>add new question</button>
	    	</>)
	    }

function Line(props){
	switch(props.type){

		case "":

			break;

		default:
			return <div>{JSON.stringify(props)}</div>
	}
}

class Course extends Component {
  constructor(props) {
    super(props);
    this.state = {
    	course: props.router.pathname.replace("/quiz","").replace("/",""),
    	questions: [],
    	file:false,
    	lineTypes:["h1","h2","p","blockquote","quiz"],
    	lines:[]
    }
  }

  componentDidMount() {
    let questions = []

    if(localStorage && localStorage.getItem('questions') === null){
   		questions = [
		{q:"According to chapter 1, this book is a revelation of Whom?",a:"Jesus Christ", ref:66001001},
		{q:"",a:"", ref:""}
		];
    	localStorage.setItem('questions', JSON.stringify(questions));
    }else{
    	questions = JSON.parse(localStorage.getItem('questions'));
    }

    this.setState({
    	questions: questions
    })
}

  render() {
  	  const q = this.props.router.query.q? this.props.router.query.q:0
  	  const updateQuestion = this.updateQuestion.bind(this)
  	  const addQuestionProp = this.addQuestionProp.bind(this)
  	  const handleNewLine = this.handleNewLine.bind(this)
    return (
	<div>
	      <Head>
	        <title>Quiz | Youth Revelation Study</title>
	        <link rel="icon" href="/favicon.ico" />
	        <meta charset="UTF-8" />
	      </Head>

		<main>
	    
		<button onClick={this.saveQuestions.bind(this)}>SAVE</button>

		<div>
		 <label htmlFor="input-file">import</label><br/>
		 <input type="file" id="input-file" onChange={this.getFile.bind(this)} />
		</div>

		{this.state.lines.map(function(line){
			return <Line {...line} />
		})}


	    		   <form onSubmit={handleNewLine}>
	    		   	<input type="hidden" name="index" />
			        <label>
			          <select name="lineType" id="lineTypes" defaultValue="p">
			          	{this.state.lineTypes.map(function(t){
			          		return <option key={t} value={t}>{t}</option>
			          	})}
					  

					</select>
			        </label>

			        <input type="submit" value="+" />
			      </form>


		</main>

	<Navigation />
</div>
    );
  }

  updateQuestion(line, propName, index, value){

  	let newQ = [...this.state.questions]

  	if(propName === undefined){
  		newQ.push({q:"",a:"", ref:""})
  	}else if(value = "delete"){
  		delete newQ[index][propName];
  	}else{
  		newQ[index][propName] = value
  	}
  	
  	this.setState({
	  questions: newQ
	})
  }


saveQuestions(){
	const txt = JSON.stringify(this.state.questions)
	localStorage.setItem('questions', txt)

	// Start file download.
	this.download(this.state.course+".json",txt);
}

download(filename, text) {
  var element = document.createElement('a');
  element.setAttribute('href', 'data:text/plain;charset=utf-8,' + encodeURIComponent(text));
  element.setAttribute('download', filename);

  element.style.display = 'none';
  document.body.appendChild(element);

  element.click();

  document.body.removeChild(element);
}

getFile(event) {
	const input = event.target
  if ('files' in input && input.files.length > 0) {
	  const questions = this.placeFileContent(input.files[0]).then((questions)=>{
	 	console.log(questions)
	  	this.setState({questions:questions})
	  })

  	}
}

placeFileContent(file) {
	return this.readFileContent(file).then(content => {
  	return JSON.parse(content)
  }).catch(error => console.log(error))
}

readFileContent(file) {
	const reader = new FileReader()
  return new Promise((resolve, reject) => {
    reader.onload = event => resolve(event.target.result)
    reader.onerror = error => reject(error)
    reader.readAsText(file)
  })
}

addQuestionProp(e){
    e.preventDefault();
    const line = e.target.line.value
    const index = e.target.index.value
    const prop = e.target.prop.value
    const val = e.target.val.value

    let newState = {...this.state}
    newState.lines[line].questions[index][prop] = val
    this.setState(newState)
}

handleNewLine(e){
    e.preventDefault();
    const lineType = e.target.lineType.value

    let newState = {...this.state}
    newState.lines.push(this.template(lineType))
    this.setState(newState)
}

template(lineType){
	switch (lineType){

		case 'quiz':
			return { title:"TITLE", questions:[{q:"",a:"", ref:""}]}
			break;
		default:

			return {"type":"p","value":""}
	}
}

}
export default withRouter(Course)

/*
1-4 Ellyanna
5-11 Jeremiah
12-16 Benjamin
17-22 Rosemary

1. Summaries
2. Conclusing of Book 1 sentence

		<Quiz line={1} questions={this.state.questions} updateQuestion={updateQuestion}/>

    */
